    public SetFiveManager(){
        setInfo();
    }

    private void setInfo(){
        try {
            //units = List.of(objectMapper.readValue(unitJSON, Unit[].class));
            items = List.of(objectMapper.readValue(itemJSON, Item[].class));
        } catch (JsonProcessingException e) {
            e.printStackTrace();
        }
    }

    public Item getItemByID(int id){
        for(Item item : this.items){
            if(item.getId() == id){
                return item;
            }
        }
        return null;
    }



